void SearchLimitsCalcSqr(mfxI16 xLoc, mfxI16 yLoc, mfxI16 *limitXleft, mfxI16 *limitXright, mfxI16 *limitYup, mfxI16 *limitYdown, ASCImDetails *dataIn, mfxI32 range, ASCMVector mv, ASCVidData *limits) {
    mfxI16
        locX = (xLoc * (mfxI16) dataIn->block_width) + (mfxI16) dataIn->horizontal_pad + mv.x,
        locY = (yLoc * (mfxI16) dataIn->block_height) + (mfxI16) dataIn->vertical_pad + mv.y;
    *limitXleft = NMAX(-locX, (mfxI16) -range);
    *limitXright = NMIN((mfxI16) dataIn->Extended_Width - ((xLoc + 1) * (mfxI16) dataIn->block_width) - (mfxI16) dataIn->horizontal_pad - mv.x, (mfxI16) range - 1);
    *limitYup = NMAX(-locY, (mfxI16) -range);
    *limitYdown = NMIN((mfxI16) dataIn->Extended_Height - ((yLoc + 1) * (mfxI16) dataIn->block_height) - (mfxI16) dataIn->vertical_pad - mv.y, (mfxI16) range - 1);
    if (limits->limitRange) {
        *limitXleft = NMAX(*limitXleft, (mfxI16) -limits->maxXrange);
        *limitXright = NMIN(*limitXright, (mfxI16) limits->maxXrange);
        *limitYup = NMAX(*limitYup, (mfxI16) -limits->maxYrange);
        *limitYdown = NMIN(*limitYdown, (mfxI16) limits->maxYrange);
    }
}

void SearchLimitsCalc(mfxI16 xLoc, mfxI16 yLoc, mfxI16 *limitXleft, mfxI16 *limitXright, mfxI16 *limitYup, mfxI16 *limitYdown, ASCImDetails *dataIn, mfxI32 range, ASCMVector mv, ASCVidData *limits) {
    mfxI16
        locX = (xLoc * (mfxI16) dataIn->block_width) + (mfxI16) dataIn->horizontal_pad + mv.x,
        locY = (yLoc * (mfxI16) dataIn->block_height) + (mfxI16) dataIn->vertical_pad + mv.y;
    *limitXleft = NMAX(-locX, (mfxI16) -range);
    *limitXright = NMIN((mfxI16) dataIn->Extended_Width - ((xLoc + 1) * (mfxI16) dataIn->block_width) - (mfxI16) dataIn->horizontal_pad - mv.x, (mfxI16) range);
    *limitYup = NMAX(-locY, (mfxI16) -range);
    *limitYdown = NMIN((mfxI16) dataIn->Extended_Height - ((yLoc + 1) * (mfxI16) dataIn->block_height) - (mfxI16) dataIn->vertical_pad - mv.y, (mfxI16) range);
    if (limits->limitRange) {
        *limitXleft = NMAX(*limitXleft, (mfxI16) -limits->maxXrange);
        *limitXright = NMIN(*limitXright, (mfxI16) limits->maxXrange);
        *limitYup = NMAX(*limitYup, (mfxI16) -limits->maxYrange);
        *limitYdown = NMIN(*limitYdown, (mfxI16) limits->maxYrange);
    }
}

mfxF32 Dist(ASCMVector vector) {
    return (mfxF32)sqrt((double)(vector.x*vector.x) + (double)(vector.y*vector.y));
}

mfxI32 DistInt(ASCMVector vector) {
    return (vector.x*vector.x) + (vector.y*vector.y);
}

void MVpropagationCheck(mfxI32 xLoc, mfxI32 yLoc, ASCImDetails dataIn, ASCMVector *propagatedMV) {
    mfxI32
        left = (xLoc * dataIn.block_width) + dataIn.horizontal_pad,
        right = (dataIn.Extended_Width - left - dataIn.block_width),
        up = (yLoc * dataIn.block_height) + dataIn.vertical_pad,
        down = (dataIn.Extended_Height - up - dataIn.block_height);
    if (propagatedMV->x < 0) {
        if (left + propagatedMV->x < 0)
            propagatedMV->x = (mfxI16) -left;
    }
    else {
        if (right - propagatedMV->x < 0)
            propagatedMV->x = (mfxI16) right;
    }

    if (propagatedMV->y < 0) {
        if (up + propagatedMV->y < 0)
            propagatedMV->y = (mfxI16) -up;
    }
    else {
        if (down - propagatedMV->y < 0)
            propagatedMV->y = (mfxI16) down;
    }
}

#define _mm_loadh_epi64(a, ptr) _mm_castps_si128(_mm_loadh_pi(_mm_castsi128_ps(a), (__m64 *)(ptr)))

#define SAD_SEARCH_VSTEP 2  // 1=FS 2=FHS

void ME_SAD_8x8_Block_Search_C(mfxU8 *pSrc, mfxU8 *pRef, int pitch, int xrange, int yrange,
    mfxU16 *bestSAD, int *bestX, int *bestY) {
    for (int y = 0; y < yrange; y += SAD_SEARCH_VSTEP) {
        for (int x = 0; x < xrange; x += SAD_SEARCH_VSTEP) {/*x++) {*/
            pmfxU8
                pr = pRef + (y * pitch) + x,
                ps = pSrc;
            mfxU16
                SAD = 0;
            for (int i = 0; i < 8; i++) {
                SAD += (mfxU16) abs(pr[0] - ps[0]);
                SAD += (mfxU16) abs(pr[1] - ps[1]);
                SAD += (mfxU16) abs(pr[2] - ps[2]);
                SAD += (mfxU16) abs(pr[3] - ps[3]);
                SAD += (mfxU16) abs(pr[4] - ps[4]);
                SAD += (mfxU16) abs(pr[5] - ps[5]);
                SAD += (mfxU16) abs(pr[6] - ps[6]);
                SAD += (mfxU16) abs(pr[7] - ps[7]);
                pr += pitch;
                ps += pitch;
            }
            if (SAD < *bestSAD) {
                *bestSAD = SAD;
                *bestX = x;
                *bestY = y;
            }
        }
    }
}

