INT ASCimageData::InitFrame(ASCImDetails *pDetails) {
    mfxU32
        imageSpaceSize = pDetails->Extended_Height * pDetails->Extended_Width,
        mvSpaceSize = (pDetails->_cheight * pDetails->_cwidth) >> 6,
        texSpaceSize = (pDetails->_cheight * pDetails->_cwidth) >> 4;

    Image.extHeight = pDetails->Extended_Height;
    Image.extWidth = pDetails->Extended_Width;
    Image.pitch = pDetails->Extended_Width;
    Image.height = pDetails->_cheight;
    Image.width = pDetails->_cwidth;
    Image.hBorder = pDetails->vertical_pad;
    Image.wBorder = pDetails->horizontal_pad;
    Image.data = NULL;
    Image.Y = NULL;
    Image.U = NULL;
    Image.V = NULL;
    //Memory Allocation
    Image.data = (mfxU8*)memalign(0x1000, imageSpaceSize);
    SAD = (mfxU16 *)memalign(0x1000, sizeof(mfxU16) * mvSpaceSize);
    Rs = (mfxU16 *)memalign(0x1000, sizeof(mfxU16) * texSpaceSize);
    Cs = (mfxU16 *)memalign(0x1000, sizeof(mfxU16) * texSpaceSize);
    RsCs = (mfxU16 *)memalign(0x1000, sizeof(mfxU16) * texSpaceSize);
    pInteger = (ASCMVector *)memalign(0x1000, sizeof(ASCMVector)  * mvSpaceSize);
    if (Image.data == NULL)  return MFX_ERR_MEMORY_ALLOC;
    if (SAD == NULL)  return MFX_ERR_MEMORY_ALLOC;
    if (Rs == NULL)  return MFX_ERR_MEMORY_ALLOC;
    memset(Rs, 0, sizeof(mfxU16) * texSpaceSize);
    if (Cs == NULL)  return MFX_ERR_MEMORY_ALLOC;
    memset(Cs, 0, sizeof(mfxU16) * texSpaceSize);
    if (RsCs == NULL)  return MFX_ERR_MEMORY_ALLOC;
    memset(RsCs, 0, sizeof(mfxU16) * texSpaceSize);
    if (pInteger == NULL)  return MFX_ERR_MEMORY_ALLOC;
    memset(pInteger, 0, sizeof(ASCMVector)  * mvSpaceSize);
    //Pointer conf.
    Image.Y = Image.data + pDetails->initial_point;
    return MFX_ERR_NONE;
}

mfxI32 ASCimageData::Close() {
    mfxI32
        res = 0;
    if (Rs)
        free(Rs);
    if (Cs)
        free(Cs);
    if (RsCs)
        free(RsCs);
    if (pInteger)
        free(pInteger);
    if (SAD)
        free(SAD);
    Rs = NULL;
    Cs = NULL;
    RsCs = NULL;
    pInteger = NULL;
    SAD = NULL;
    free(Image.data);
    Image.data = NULL;
    Image.Y = NULL;
    Image.U = NULL;
    Image.V = NULL;

    return res;
}

INT ASC::Setup_Environment(const mfxFrameInfo& FrameInfo) {
    mfxStatus
        sts = MFX_ERR_NONE;

    dataIn->accuracy = 1;

    dataIn->layer->Original_Width = SMALL_WIDTH;
    dataIn->layer->Original_Height = SMALL_HEIGHT;
    dataIn->layer->_cwidth = SMALL_WIDTH;
    dataIn->layer->_cheight = SMALL_HEIGHT;

    dataIn->layer->block_width = 8;
    dataIn->layer->block_height = 8;
    dataIn->layer->vertical_pad = 0;
    dataIn->layer->horizontal_pad = 0;
    dataIn->layer->Extended_Height = dataIn->layer->vertical_pad + SMALL_HEIGHT + dataIn->layer->vertical_pad;
    dataIn->layer->Extended_Width = dataIn->layer->horizontal_pad + SMALL_WIDTH + dataIn->layer->horizontal_pad;
    dataIn->layer->pitch = dataIn->layer->Extended_Width;
    dataIn->layer->Height_in_blocks = dataIn->layer->_cheight / dataIn->layer->block_height;
    dataIn->layer->Width_in_blocks = dataIn->layer->_cwidth / dataIn->layer->block_width;
    dataIn->layer->sidesize = dataIn->layer->_cheight + (1 * dataIn->layer->vertical_pad);
    dataIn->layer->initial_point = (dataIn->layer->Extended_Width * dataIn->layer->vertical_pad) + dataIn->layer->horizontal_pad;
    dataIn->layer->MVspaceSize = (dataIn->layer->_cheight / dataIn->layer->block_height) * (dataIn->layer->_cwidth / dataIn->layer->block_width);
    //////////////////////////
    // allocation of temp buffer
    scdRequest.NumFrameMin = scdRequest.NumFrameSuggested = 0;
    memset(&scdRequest, 0, sizeof(scdRequest));
    scdRequest.Info = FrameInfo;
    scdRequest.Info.FourCC = MFX_FOURCC_NV12;

    if (Query_ASCCmDevice()) {
        // this is how MSDK does it using m_ioMode & m_IOPattern
        scdRequest.Type = MFX_MEMTYPE_FROM_VPPOUT | MFX_MEMTYPE_DXVA2_PROCESSOR_TARGET;
        scdRequest.Type |= MFX_MEMTYPE_INTERNAL_FRAME;
        //    if (m_ioMode == IOMode::D3D_TO_SYS || m_ioMode == IOMode::SYS_TO_SYS)
        //    {
        //      It always goes to SYS
        //    }
        /*if (io_pattern & MFX_IOPATTERN_OUT_OPAQUE_MEMORY)
        scdRequest.Type |= MFX_MEMTYPE_OPAQUE_FRAME;*/

        scdRequest.NumFrameMin = scdRequest.NumFrameSuggested = 1;
        if (0 == scdRequest.NumFrameMin)
            return MFX_ERR_INVALID_VIDEO_PARAM;

        // create a pool of surfaces & map pointers to a pool of pointers
        m_pscdMFXSurfacePool.resize(scdRequest.NumFrameMin);
        m_scdMFXSurfacePool.resize(scdRequest.NumFrameMin);
        for (size_t idx = 0; idx < scdRequest.NumFrameMin; ++idx)
        {
            scdmfxFrame = m_pscdMFXSurfacePool[idx] = &(m_scdMFXSurfacePool[idx]);
            memset(m_pscdMFXSurfacePool[idx], 0, sizeof(mfxFrameSurface1));
            // lets pass pointers to QfIn
        }

        // now, allocate (depending on a type)
        memset(&m_scdmfxAlocResponse, 0, sizeof(m_scdmfxAlocResponse));
        if (scdRequest.Type & MFX_MEMTYPE_OPAQUE_FRAME)
            sts = scdCore->AllocFrames(&scdRequest, &m_scdmfxAlocResponse, &(m_pscdMFXSurfacePool[0]), scdRequest.NumFrameMin);
        else
        {
            // this code is borrowed from vpphw, init, when out == system memory,
            //if (D3D_TO_SYS == m_ioMode || SYS_TO_SYS == m_ioMode) // [OUT == SYSTEM_MEMORY]
            sts = scdCore->AllocFrames(&scdRequest, &m_scdmfxAlocResponse, scdRequest.Info.FourCC != MFX_FOURCC_NV12);
            /*        else
            sts = scdCore->AllocFrames(&scdRequest, &m_scdmfxAlocResponse, false)*/;
        }
        MFX_CHECK(MFX_ERR_NONE == sts, MFX_WRN_PARTIAL_ACCELERATION);

        // clear & bind video-surfaces to mfxFrameSurface1 structures
        mfxMemId* pmids = m_scdmfxAlocResponse.mids;
        for (auto it = m_pscdMFXSurfacePool.begin(); it != m_pscdMFXSurfacePool.end(); ++it)
        {
            (*it)->Info = scdRequest.Info;
            (*it)->Info.FourCC = MFX_FOURCC_NV12;
            (*it)->Data.MemType = scdRequest.Type;
            (*it)->Data.MemId = *pmids++;
            // set to extreamly high value to identify possible incorrectness
            // with settings
            (*it)->Data.FrameOrder = 0xFFFFFFFF;
            (*it)->Data.TimeStamp = 0xFFFFFFFF;
        }

        mfxHDLPair handle;
        // if a surface is opaque, need to extract normal surface
        mfxFrameSurface1 *pSurf = scdCore->GetNativeSurface(scdmfxFrame, false);
        scdmfxFrame = pSurf ? pSurf : scdmfxFrame;
        // GetFrameHDL is used as QfIn[].mfxFrme is allocated via call to Core Alloc functoin
        MFX_CHECK_STS(scdCore->GetFrameHDL(scdmfxFrame->Data.MemId, reinterpret_cast<mfxHDL*>(&handle)));

        INT res = device->CreateSurface2D(reinterpret_cast<AbstractSurfaceHandle>(handle.first), pSurface);
        CM_CHK_RESULT(res);
        res = pSurface->GetIndex(pIdxSurf);
        CM_CHK_RESULT(res);
    }
    else {
        scdmfxFrame = NULL;
        pSurface = NULL;
        pIdxSurf = NULL;
    }

    return sts;
}

