INT ASC::InitGPUsurf(VideoCORE *core, CmDevice *m_cmDevice, bool useGPUsurf) {
    INT res = CM_SUCCESS;
    Reset_ASCCmDevice();
    if (!core || !useGPUsurf) {
        device = NULL;
        program = NULL;
    }
    else if (useGPUsurf) {
        if (m_cmDevice) {
            extCmDevice = true;
            device = m_cmDevice;
        }
        else {
            extCmDevice = false;
            res = CreateCmDevicePtr(core, device, &version);
            SCD_CHECK_CM_ERR(res, MFX_ERR_DEVICE_FAILED);
        }
        Set_ASCCmDevice();
        res = device->CreateQueue(queue);
        SCD_CHECK_CM_ERR(res, MFX_ERR_DEVICE_FAILED);
        hwSize = 4;
        res = device->GetCaps(CAP_GPU_PLATFORM, hwSize, &hwType);
        SCD_CHECK_CM_ERR(res, MFX_ERR_DEVICE_FAILED);

#if CMRT_EMU
        res = device->LoadProgram((void *)genx_skl_h264_scd, sizeof(genx_skl_h264_scd), program);
#else
        if (hwType == PLATFORM_INTEL_HSW)
            res = device->LoadProgram((void *)genx_hsw_h264_scd, sizeof(genx_hsw_h264_scd), program, "nojitter");
        else if (hwType == PLATFORM_INTEL_BDW)
            res = device->LoadProgram((void *)genx_bdw_h264_scd, sizeof(genx_bdw_h264_scd), program, "nojitter");
        else if (hwType == PLATFORM_INTEL_SKL || hwType == PLATFORM_INTEL_KBL)
            res = device->LoadProgram((void *)genx_skl_h264_scd, sizeof(genx_skl_h264_scd), program, "nojitter");
        else
            return CM_FAILURE;
#endif
        CM_CHK_RESULT(res);
    }
    return res;
}
