void ASC::GeneralBufferRotation() {
    ASCVidSample
        *videoTransfer;
    ASCTSCstat
        *metaTransfer;

#if (ASCVIDEOSTATSBUF > 3)
    if ((support->logic[ASCSceneVariation_frame_data]->copyFrameDelay == 1 && support->logic[ASCprevious_frame_data]->Schg == 1) || support->logic[ASCSceneVariation_frame_data]->Schg == 1) {
        videoTransfer = videoData[0];
        videoData[0] = videoData[ASCVIDEOSTATSBUF - 2];
        videoData[ASCVIDEOSTATSBUF - 2] = videoTransfer;
        memcpy(support->logic[ASCSceneVariation_frame_data], support->logic[ASCprevious_frame_data], sizeof(ASCTSCstat));
        support->logic[ASCSceneVariation_frame_data]->Schg = 1;
    }
#endif
    if (support->logic[ASCcurrent_frame_data]->repeatedFrame) {
        videoData[ASCReference_Frame]->frame_number = videoData[ASCCurrent_Frame]->frame_number;
        support->logic[ASCcurrent_frame_data]->Schg = 0;
        support->logic[ASCprevious_frame_data]->Schg = 0;
        support->logic[ASCprevious_previous_frame_data]->Schg = 0;
    }
    else {
        videoTransfer = videoData[0];
        videoData[0] = videoData[1];
        videoData[1] = videoTransfer;

        metaTransfer = support->logic[ASCprevious_previous_frame_data];
        support->logic[ASCprevious_previous_frame_data] = support->logic[ASCprevious_frame_data];
        support->logic[ASCprevious_frame_data] = support->logic[ASCcurrent_frame_data];
        support->logic[ASCcurrent_frame_data] = metaTransfer;
    }
        
}



void ExtendBorders(pmfxU8 ImageToExtend, ASCImDetails imageInfo) {
    pmfxU8
        bRow,
        tRow;
    mfxI32
        row,
        borderh,
        borderv;
    mfxU8
        uLeftFillVal,
        uRightFillVal,
        *pByteSrc,
        *pSrcLeft,
        *pSrcRight;
    borderh = imageInfo.horizontal_pad;
    borderv = imageInfo.vertical_pad;
    // section 1 at bottom
    // obtain pointer to start of bottom row of original frame
    pByteSrc = ImageToExtend + imageInfo.endPoint + imageInfo.horizontal_pad + imageInfo.horizontal_pad;
    bRow = pByteSrc - imageInfo.Extended_Width;
    for (row = 0; row<borderv; row++) {
        memcpy(pByteSrc, bRow, imageInfo._cwidth);
        pByteSrc = pByteSrc + imageInfo.Extended_Width;
    }
    // section 2 on left and right
    // obtain pointer to start of first row of original frame
    pByteSrc = ImageToExtend + imageInfo.initial_point;
    for (row = 0; row< (mfxI32)imageInfo.sidesize; row++, pByteSrc += imageInfo.Extended_Width) {
        // get fill values from left and right columns of original frame
        uLeftFillVal = *pByteSrc;
        pSrcLeft = pByteSrc - borderh;
        pSrcRight = pByteSrc + imageInfo._cwidth;
        uRightFillVal = *(pSrcRight - 1);

        // fill all bytes on both edges
        memset(pSrcLeft, uLeftFillVal, borderh);
        memset(pSrcRight, uRightFillVal, borderh);
    }
    // section 3 at top
    // obtain pointer to top row of original frame, less expand pels
    pByteSrc = ImageToExtend + imageInfo.initial_point - borderh;
    tRow = ImageToExtend;
    for (row = 0; row<borderv; row++, tRow += imageInfo.Extended_Width)
        memcpy(tRow, pByteSrc, imageInfo.Extended_Width);
}

